var up = '('
var down = ')'

func main() {
	floor := 0
	for pos, char := range input {
		if char == up {
			floor++
		} else if char == down {
			floor--
		}

		if char == down && floor == -1 {
			println("going to the basement at move number", pos+1)
		}
	}

	println("floor: ", floor)
}
